pragma solidity ^0.5.0;

import './Admin.sol';

contract Culturestake is Admin {
  mapping (bytes32 => mapping (address => bool)) festivals;
  mapping (address => VotingBooth) votingBooths;

  struct VotingBooth {
    bool active;
    bytes32 festival;
    mapping (uint256 => bool) nonces;
  }

  struct Festival {
    mapping (address => bool) votes;
  }

  constructor() public {

  }

  function validateVotingBooth(
    bytes32 festival,
    uint256 nonce,
    uint8 sigV,
    bytes32 sigR,
    bytes32 sigS) internal {
      bytes32 h = keccak256(abi.encodePacked(byte(0x19), byte(0), festival, nonce));
      address addressFromSig = ecrecover(h, sigV, sigR, sigS);
      require(votingBooths[addressFromSig].active);
      require(votingBooths[addressFromSig].festival == festival);
      require(!votingBooths[addressFromSig].nonces[nonce]);
      votingBooths[addressFromSig].nonces[nonce] = true;
  }

  function initVotingBooth(
    bytes32 festival,
    address booth) public authorized {
      votingBooths[booth].active = true;
      votingBooths[booth].festival = festival;
  }

  function deactivateVotingBooth(address booth) public authorized {
    votingBooths[booth].active = false;
  }

  function vote() public {

  }

}
